import React from 'react'
import fetch from 'node-fetch'

var parser = require('fast-xml-parser');

function fetchData(url) {
  return new Promise((resolve, reject) => {
    fetch(url)
      .then(res => res.text())
      .then(body => resolve(body))
  })
}

const MainComponent = (props) => {
  let books = [props.GoodreadsResponse.book];
  console.log(books);
  let bookElements = [];

  for (let i = 0; i < books.length; i++) {
    bookElements.push(
      <div className="book">
        {books[i].title} + " by " +   {books[i].authors[0]}
      </div>
    );
  }

  return (
    <div>
      {bookElements}
    </div>
  )
}

MainComponent.getInitialProps = async () => {
  let xmlData = await fetchData("https://www.goodreads.com/book/show?key=P89RUgLvpIk3VVqzk54Kcw&id=26050611-good-profit");
  let data = parser.parse(xmlData);

  return parser.parse(xmlData);
}

export default MainComponent
